--
-- Create procedure `Proc_Material_GetMaxCode`
--
CREATE PROCEDURE Proc_Material_GetMaxCode (IN p_Prefix varchar(20))
SQL SECURITY INVOKER
COMMENT 'Lấy mã nguyên vật liệu mới - nlnhat (17/08/2023)'
BEGIN
  DECLARE maxNumber int;

  --  Lấy số lớn nhất theo Prefix
  SELECT
    MAX(CAST(SUBSTRING(m.MaterialCode, LENGTH(p_Prefix) + 1) AS UNSIGNED)) INTO maxNumber
  FROM material m
  WHERE m.MaterialCode REGEXP CONCAT('^', p_Prefix, '[0-9]+$');

  -- Nếu chưa có mã theo Prefix thì set = 0
  IF maxNumber IS NULL THEN
    SET maxNumber = 0;
  END IF;

  SELECT
    maxNumber;
END
$$

