--
-- Create procedure `Proc_Material_Filter`
--
CREATE PROCEDURE Proc_Material_Filter (IN p_KeySearch varchar(255),
IN p_PageNumber int,
IN p_PageSize int,
IN p_QuerySort varchar(10000),
IN p_QueryFilter varchar(10000),
OUT p_TotalRecord int,
OUT p_AllRecord int,
OUT p_PageNumberOut int)
SQL SECURITY INVOKER
COMMENT 'Lọc, tìm kiếm, sắp xếp, phân trang nguyên vật liệu - nlnhat (17/08/2023)'
BEGIN
  DECLARE offset int;
  DECLARE totalPage int;

  IF (p_KeySearch IS NULL
    OR p_KeySearch = '') THEN
    SET @query = CONCAT(
    'CREATE TEMPORARY TABLE Temp_Material
          SELECT * FROM View_Material vm
          WHERE 1=1 ', p_QueryFilter, ' ',
    p_QuerySort, ';');

    PREPARE proc FROM @query;
    EXECUTE proc;
  ELSE
    SET @query = CONCAT(
    'CREATE TEMPORARY TABLE Temp_Material
          SELECT * FROM View_Material vm 
           WHERE ((vm.MaterialCode LIKE "%', p_KeySearch, '%") 
             OR (vm.MaterialName LIKE "%', p_KeySearch, '%") 
             OR (vm.Note LIKE "%', p_KeySearch, '%")) ',
    p_QueryFilter, ' ',
    p_QuerySort, ';');

    PREPARE proc FROM @query;
    EXECUTE proc;
  END IF;

  -- Đếm số bản ghi được lọc
  SELECT
    COUNT(*) INTO p_TotalRecord
  FROM Temp_Material;

  -- Đếm toàn bộ số bản ghi
  SELECT
    COUNT(*) INTO p_AllRecord
  FROM View_Material;

  -- Phân trang
  IF (p_PageSize IS NOT NULL
    AND p_PageSize > 0) THEN
    SET totalPage = CEIL(p_TotalRecord / p_PageSize);
    SET p_PageNumberOut = LEAST(p_PageNumber, totalPage);
    SET offset = (p_PageNumberOut - 1) * p_PageSize;

    SELECT
      p_PageNumberOut INTO p_PageNumberOut;

    SELECT
      *
    FROM Temp_Material
    LIMIT offset, p_PageSize;

  ELSE
    SET p_PageNumberOut = 1;
    SELECT
      p_PageNumberOut INTO p_PageNumberOut;
    SELECT
      *
    FROM Temp_Material;

  END IF;

  DROP TEMPORARY TABLE Temp_Material;

  DEALLOCATE PREPARE proc;
END
$$

