--
-- Create function `initials`
--
CREATE FUNCTION initials (str text, expr text)
RETURNS text CHARSET utf8mb3
SQL SECURITY INVOKER
NO SQL
BEGIN
  DECLARE result text DEFAULT '';
  DECLARE buffer text DEFAULT '';
  DECLARE i int DEFAULT 1;
  IF (str IS NULL) THEN
    RETURN NULL;
  END IF;
  SET buffer = TRIM(str);
  WHILE i <= LENGTH(buffer) DO
    IF SUBSTR(buffer, i, 1) REGEXP expr THEN
      SET result = CONCAT(result, SUBSTR(buffer, i, 1));
      SET i = i + 1;
      WHILE i <= LENGTH(buffer)
        AND SUBSTR(buffer, i, 1) REGEXP expr DO
        SET i = i + 1;
      END WHILE;
      WHILE i <= LENGTH(buffer)
        AND SUBSTR(buffer, i, 1) NOT REGEXP expr DO
        SET i = i + 1;
      END WHILE;
    ELSE
      SET i = i + 1;
    END IF;
  END WHILE;
  RETURN result;
END
$$

