-- Create function `acronym`
--
CREATE FUNCTION acronym (str text)
RETURNS text CHARSET utf8mb3
SQL SECURITY INVOKER
NO SQL
BEGIN
  DECLARE result text DEFAULT '';
  SET result = initials(str, '[[:alnum:]]');
  RETURN result;
END
$$

